drop table if exists conference;
create table conference (
    ID int primary key,
    Year int,
    Month text
);
create index date_ix on conference (Year, Month);

drop table if exists session;
create table session (
    ID int primary key,
    Title text,
    ConferenceID int
);

drop table if exists talk;
create table talk (
    ID int primary key,
    SessionID int,
    Title text,
    Speaker text,
    Date text,
    AudioUrl text,
    VideoUrl text
);
